INSERT INTO products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) VALUES (1, 2, 'Matrox G200 MMS', 'Matrox 的芯片技术在新的 Tech Source GFX 200 系列发挥功能，采用首创以一张 PCI 图形卡支持基于Sun Microsystems Solaris平台，支持多达四个 DVI 数字液晶显示屏。GFX 200 系列，有 16 MB（支持两个显示屏）或 32 MB（支持四个显示屏）SGRAM 可选择。以 Matrox G200 多屏幕显示系列的技术为基础，此技术也广受金融界认可，并支持金融界权威公司如 Barclays、富达投资和路透社等。', 'www.matrox.com/mga/products/g200_mms/home.cfm', 0),
(2, 2, 'Matrox G400 32MB', 'G400显卡绝对是显卡历史上永恒的经典产品，其优良的2D画质在如今都是出类拔萃的！也正因为如此，它也成为许多2D作图用户的首选显卡。在很多二手产品论坛上，G400都是炙手可热的东西。G400采用SGRAM显存，容量为16M或32M，AGP-4X接口，对2D画质要求很高或对Matrox显卡情有独衷的朋友可以关注一下。', 'www.matrox.com/mga/products/mill_g400/home.htm', 0),
(3, 2, '人体工程学鼠标', '人体工程学鼠标又分标准外壳和半透明外壳两种，这些鼠标功能相同，不过手感不同，人体工程学鼠标比较顺手。不加载专用的驱动程序也可以使用滚轮功能。专用的驱动程序可以通过网络获得目前的最新版本。', 'www.microsoft.com/hardware/mouse/intellimouse.asp', 0),
